import React, { Component } from 'react';
import './App.css';
import Clarifai from 'clarifai';
import Navigation from './components/Navigation/Navigation'
import FaceRecognition from './components/FaceRecognition/FaceRecognition'
import Logo from './components/Logo/Logo'
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm'
import Rank from './components/Rank/Rank'
import Particles from 'react-particles-js';

const app = new Clarifai.App({
  apiKey:'831a251bab8d4b8fb8693919be088ef8'
});

const particlesOptions = {
  particles: {
    number: {
      value: 100,
      density: {
        enable: true,
        value_area: 800
      }
    }
  }
}

class App extends Component {
  constructor() {
    super();
    this.state = {
      input: '',
      imageUrl:'',
      dimensions: [],
    }
  }

  calculateFaceLocation = (data) => {
    const image = document.getElementById('inputimage');
    const width = Number(image.width);
    const height = Number(image.height);
    const clarifaiFace = data.outputs[0].data.regions
    const allFaces = clarifaiFace.map( face => {
      return({
      leftCol: face.region_info_bounding_box.left_col * width,
      topRow : face.region_info_bounding_box.top_row * height,
      rightCol: width - (face.region_info_bounding_box.right_col * width),
      bottomRow: height - (face.region_info_bounding_box.bottom_row * height)
    })
  })
    return allFaces
  }

  displayFaceBox = (dimensions) => {
    this.setState(prevState => ({
      dimensions: [...prevState.dimensions, ...dimensions]
    }))
  }

  onInputChange = (event) => {
    this.setState({input: event.target.value });
  }

  onButtonSubmit = () => {
    // this.setState({imageUrl: this.state.input},{dimensions: []});
    this.setState({imageUrl: this.state.input});

    app.models
    .predict(
      Clarifai.FACE_DETECT_MODEL,
      this.state.input)
    .then(response => 
        this.displayFaceBox(this.calculateFaceLocation(response))
      )
    .catch(err => console.log(err));
  }

  render() {
  return (
    <div className="App">
      <Particles className ='particles'
       params={particlesOptions}
      />
      <Navigation />
      <Logo /> 
      <Rank />
      <ImageLinkForm 
        onInputChange={this.onInputChange} 
        onButtonSubmit = {this.onButtonSubmit}
        /> 
      <FaceRecognition dimensions={this.state.dimensions} imageUrl = {this.state.imageUrl} />
     </div>
    );
  }
}

export default App;
